import React, { useState, useEffect } from 'react';

const Character = () => {
  const [posX, setPosX] = useState(144);
  const [posY, setPosY] = useState(144);
  const [direction, setDirection] = useState('down');
  const [isMoving, setIsMoving] = useState(false);
  const speed = 9;

  useEffect(() => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0, img.width, img.height);
    };

    const isColliding = (x, y) => {
      const pixel = ctx.getImageData(x, y, 1, 1).data;
      const isWalkable = pixel[0] === 255 && pixel[1] === 255 && pixel[2] === 255;
      return !isWalkable;
    };

    const handleKeyDown = (event) => {
      let newX = posX;
      let newY = posY;

      switch (event.key) {
        case 'ArrowUp':
          newY = Math.max(posY - speed, 0);
          setDirection('up');
          break;
        case 'ArrowDown':
          newY = Math.min(posY + speed, canvas.height - 16);
          setDirection('down');
          break;
        case 'ArrowLeft':
          newX = Math.max(posX - speed, 0);
          setDirection('left');
          break;
        case 'ArrowRight':
          newX = Math.min(posX + speed, canvas.width - 16);
          setDirection('right');
          break;
        default:
          break;
      }

      if (!isColliding(newX, newY)) {
        setPosX(newX);
        setPosY(newY);
        setIsMoving(true);
      }
    };

    const handleKeyUp = () => {
      setIsMoving(false);
    };

    document.addEventListener('keydown', handleKeyDown);
    document.addEventListener('keyup', handleKeyUp);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.removeEventListener('keyup', handleKeyUp);
    };
  }, [posX, posY]);

  return (
    <div
      className={`character ${direction} ${isMoving ? 'moving' : ''}`}
      style={{
        top: posY + 'px',
        left: posX + 'px',
      }}
    ></div>
  );
};

export default Character;
